
public class ShorthandOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m=4,n=5;
		m = ++n ; // Pre increment
		System.out.println(m);
		System.out.println(n);
		int m1=4,n1=5;
		m1 = n1++ ; // Post increment
		System.out.println(m1);
		System.out.println(n1);
		

	}

}
